import React, {useState,setState} from "react";
import styles from './mainPage.module.css'
import axios from 'axios';


function MainPage() {
    const [movie, setMovie] = useState({});
    const [error, setError] = useState(' ');
    const [movies, setMovies] = useState({});



    const onEnter = async (event) => {
        if(event.charCode === 13) { 
            const key = "9df453a9"
            const url = `http://www.omdbapi.com/?t=${event.target.value}&apikey=${key}`;
            try {
                try {
                var isExist = await axios.get('http://localhost:8000/getMovie', event.target.value)
                }
                catch(e) {
                    console.log(e);
                }

                const response = await fetch(url);
                const data = await response.json();
                setMovie(data)
                // console.log(data);
                if(data.Response === "False") {
                    setError("Movie not found!")
                }
                else {
                    if(isExist === null) {
                        let m = {
                            title: data.Title,
                        }
                        await axios.post('http://localhost:8000/addMovie', m);
                    }
                    setError("");
                }
              } catch (e) {
                  setError(e)
              } 
        }
    }

    const handlerForUpVote = async(event,m) => {
        event.preventDefault();
        if(m !== null) {
            console.log(m);
            const mov = await axios.get('http://localhost:8000/getMovie', m)
            console.log(mov);
            await axios.put('http://localhost:8000/updateMovie', mov);
        }
    }

    


            return (
                <div>
                    <nav>
                        <h1 className={styles.title}>Searching</h1> 
                        <div className={styles.search}>
                            <input  type = "text" placeholder="Search ..." name="search" onKeyPress = {onEnter} />
                        </div>
                        <content>
                            <span className = {movie.Response === 'False' ? styles.invis : styles.upVote} onClick = {(e) => handlerForUpVote(e,movie)}>&#x2191; 5</span>
                            <img className = {styles.picture} src = {movie.Poster}  alt = ""/>
                            <h1 className= {styles.Mtitle}>{movie.Title}</h1>
                            <p className= {styles.desc}> {movie.Plot}</p>
                            <h4 className= {styles.err}>{error}</h4>
                        </content>
                    </nav>

                </div>
            )
}

export default MainPage;
