import React, {useState,setState} from "react";
import styles from './mainPage.module.css'

function MainPage() {
    const [movie, setMovie] = useState({});


    
    const onEnter = async (event) => {
        if(event.charCode === 13) { 
            const key = "9df453a9"
            const url = `http://www.omdbapi.com/?t=${event.target.value}&apikey=${key}`;
            try {
                const response = await fetch(url);
                 const data = await response.json()
                 setMovie(data)
                 console.log(movie)
              } catch (e) {
                  console.error(e.toString);
              } 
        }
    }

        return (
            <div>
                <nav>
                    <h1 className={styles.title}>Searching</h1> 
                    <div className={styles.search}>
                        <input  type = "text" placeholder="Search ..." name="search" onKeyPress = {onEnter} />
                    </div>
                    <table className={styles.table}>
                        <tr>
                            <td> <span className = {styles.upVote}>&#x2191;</span> </td>
                            <td> <img className = {styles.picture} src = {movie.Poster}  alt = ""/> </td>
                            <td rowSpan="2"> <h1 className= {styles.Mtitle}>{movie.Title}</h1> </td>
                            <p className= {styles.desc}> {movie.Plot}</p> 
                        </tr>
                        <tr></tr>
                        
                        
                    </table>
                </nav>

            </div>
        )
}

export default MainPage;
